  
  
  $(document).ready(function () {
    // Inicializar Smart Cart
    $('#carrito-contador').smartCart({
        lang: {
            cartTitle: "Carrito",
            checkout: 'Pagar',
            clear: 'Borrar',
            subtotal: 'Subtotal:',
            cartRemove: '×',
            cartEmpty: 'No hay nada por acá!<br />Elegí tus productos'
        }
    });

    // Función para actualizar el contador del ícono de carrito
    function actualizarContadorCarrito() {
        let totalCantidad = 0;

        // Iteramos sobre cada producto en el carrito
        $('#smartcart .sc-product-item').each(function () {
            const cantidad = parseInt($(this).find('.sc-cart-item-qty').val());
            totalCantidad += isNaN(cantidad) ? 0 : cantidad;
        });

        // Actualizamos el número del carrito
       $('#carrito-contador').text(totalCantidad);
    }

    // Detectar cambios en el carrito
    $('#smartcart').on('click change', '.sc-add-to-cart, .sc-cart-remove, .sc-cart-item-qty', function () {
        setTimeout(actualizarContadorCarrito, 100);
    });

    // Cuando se vacía todo el carrito
    $('#smartcart').on('click', '.sc-empty-cart', function () {
        setTimeout(function () {
            $('#carrito-contador').text(0);
        }, 100);
    });
  });

